import React, { Component } from 'react'
import Reacr from 'axios'
import axios from 'axios'

export class User extends Component {

    constructor(props) {
        super(props)

        this.state = {
             usrs:[

                    {
                        login: "mojombo",
                        id: 1,
                        node_id: "MDQ6VXNlcjE=",
                        avatar_url: "https://avatars.githubusercontent.com/u/1?v=4",
                        gravatar_id: "",
                        url: "https://api.github.com/users/mojombo",
                        html_url: "https://github.com/mojombo",
                 }

             ]
        }
    }
    getUsers =() => {
        axios.get('https://api.github.com/users')
        .then(respense => {
            this.setState({
                usrs : respense.data
            })
        })
    }
    componentDidMount(){
        this.getUsers();
    }

    render() {
        return (
            <div>
                <div className="row">
                {this.state.usrs.map(usrs => (  //map  un poucle comme  for
                    <div className="col-md-4">
                        <div className="card text-white bg-secondary mb-3">
                <img className="card-img-top" src={usrs.avatar_url} alt=""/>
                <div className="card-body">
                  <h4 className="card-title">{usrs.login}</h4>
                  <p className="card-text">
                      <a href={usrs.html_url} className="btn btn-success">show more</a>

                  </p>
                </div>
                </div>
                    </div>

                ))}

            </div>
            </div>
        )
    }
}

export default User;
