import React, { Component } from 'react'

export class Serch extends Component {
    state ={
        search : "salam"
    }

    hand = (e) => {
        this.setState({
            search : e.target.value
        })
    }

    searchUp =(e)=>{
        e.preventDefault();
        this.props.getSearch(this.state.search)

    }
    render() {

        const { search }=this.state;
        return ( 

            <div>

               <form onSubmit={this.searchUp}>
                    <div className="form-group form-inline my-2 my-lg-0">


                        <input value={search} onChange={this.hand} id="search" className="form-control mr-sm-2" type="text"/>
                        <button type="submit" class="btn btn-outline-primary my-2 my-sm-0">seach</button>

                    </div>

                </form>
            </div>
        )
    }
}

export default Serch
